function setup() {
  createCanvas(400, 300);
  background(222);

  noStroke();
  // maakt gebruik van hexadecimale kleurcode's
  // voorbeeldkleuren: https://www.color-hex.com/
  fill('#f00');
  rect(0, 0, 400, 100);

  // kleurwaarden kan ook in R G B
  fill(255, 255, 255);
  rect(0, 100, 400, 100);

  // dit is blauw want
  // R     G      B
  // 0     0     255
  // betekent geen rood, geen groen
  // alleen maar blauw
  fill(0, 0, 255);
  rect(0, 200, 400, 100);
}

